for tc in range(int(input())):
    N, d = map(int, input().split())
    if (d < 20):
        ans = 0
        for num in pre[d-1]:
            if (num < N): ans += num
            else: break
        print(ans)
        continue

    
    ans = set()
    for i in range(1, int(N**0.5)):
        cur = i**2 + (i+d)**2
        if (cur >= N): break
            
        k = i + d*2
        while (cur < N):
            if (isPalindrome(cur)): ans.add(cur)
                
            cur += k * k
            k += d
                
    print(sum(ans))
