#pragma GCC optimize("Ofast")
#pragma GCC target("avx,avx2,fma")

#include <cstdio>
#include <vector>

using std::vector;

const int N = 3000000;
const long long MOD = 10000000000000000LL;

// Tune this
const int SEG_SIZE = 10000;

bool is_prime[N];
int primes[2000];
int prime_cnt;
void sieve(int n) {
  is_prime[2] = true;
  for (int i = 3; i <= n; i += 2) is_prime[i] = true;
  primes[0] = 2;
  prime_cnt = 1;

  for (int st = 3; st <= n; st += SEG_SIZE) {
    int en = (st + SEG_SIZE < n+1 ? st + SEG_SIZE : n+1);
    for (int i = 1; i < prime_cnt; ++i) {
      int p = primes[i];
      if (p*p >= en) break;
      int s = (st <= p ? p+p : (st/p + (st%p != 0)) * p);
      for (int j = s; j < en; j += p) {
        is_prime[j] = false;
      }
    }
    if (st < 7000) for (int i = st; i < en; i += 2) {
      if (!is_prime[i]) continue;
      primes[prime_cnt++] = i;
      if (1LL*i*i < en) for (int j = 1LL*i*i; j < en; j += i) {
        is_prime[j] = false;
      }
    }
  }
}

vector<int> idxs[7007];

int q;
long long ans[105];

void read() {
  scanf("%d", &q);
  for (int i = 0; i < q; ++i) {
    int n;
    scanf("%d", &n);
    idxs[n].push_back(i);
  }
}

long long cnt[N];

void generate() {
  int sum = 0;
  long long res = 0;
  cnt[0] = 1;

  int pp = 0;
  for (int p: primes) {
    if (p > 7000) break;

    for (int i = sum; i >= 0; --i) {
      cnt[i+p] += cnt[i];
      if (cnt[i+p] >= MOD) cnt[i+p] -= MOD;

      if (is_prime[i+p]) {
        res += cnt[i];
        if (res >= MOD) res -= MOD;
      }
    }

    printf("%lldLL,", res);
    sum += p;
  }
}

